// Access the first input item in 'Run Once' mode
const data = $input.first().json;

const output = [];

for (let i = 1; i <= 6; i++) {
  const headingKey = `Slide ${i} Heading`;

  // Get all matching subheadings
  const subheadingKeys = Object.keys(data).filter(key =>
    key.startsWith(`Slide ${i} Sub Heading`)
  );

  const subheadings = subheadingKeys
    .map(key => data[key])
    .filter(value => value && value.trim());

  // Handle CTA keys for Slide 6
  if (subheadings.length === 0 && i === 6) {
    const ctaKeys = Object.keys(data).filter(key =>
      key.startsWith(`Slide 6 CTA`)
    );
    ctaKeys.sort(); // Maintain CTA order
    subheadings.push(
      ...ctaKeys.map(key => data[key]).filter(value => value && value.trim())
    );
  }

  if (data[headingKey] || subheadings.length > 0) {
    output.push({
      json: {
        heading: data[headingKey] || '',
        subheadings,
        slideIndex: i  // ✅ Add the slide index here
      },
    });
  }
}

return output;

const inputItems = $input.all();

// Use a Set to deduplicate media IDs
const uniqueIds = new Set();

for (const item of inputItems) {
  const id = item.json.id;
  if (id && typeof id === 'string') {
    uniqueIds.add(id.trim());
  }
}

// Convert Set to array for Facebook `children`
const children = Array.from(uniqueIds);

// Return output in correct format
return [
  {
    json: {
      children
    }
  }
];
